mapper.select:
SELECT r.report_id AS reportId, r.writer_id AS writerId, e.name AS writerName, r.title, -- r.content, r.created_date AS createdDate, -- r.modified_date AS modifiedDate, r.approver_id AS approverId, r.approver_name AS approverName, r.status -- r.reject_reason AS rejectReason FROM reports r JOIN employees e ON r.writer_id = e.employee_id
r.approver_id = #{approverId}
AND DATE_FORMAT(r.created_date, '%Y-%m') BETWEEN #{startYearMonth} AND #{endYearMonth}
AND r.writer_id = #{writerId}

SELECT report_id AS reportId, writer_id AS writerId, title, created_date AS createdDate, approver_name AS approverName, status FROM reports
writer_id = #{writerId}
AND ( title LIKE CONCAT('%', #{keyword}, '%') OR content LIKE CONCAT('%', #{keyword}, '%') )
AND ( approver_name LIKE CONCAT('%', #{keyword}, '%') )
AND DATE_FORMAT(created_date, '%Y-%m') BETWEEN #{reportStart} AND #{reportEnd}
ORDER BY created_date DESC LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(writer_id) FROM reports

    writer_id = #{writerId}

        AND (
        title LIKE CONCAT('%', #{keyword}, '%') OR
        content LIKE CONCAT('%', #{keyword}, '%')
        )


        AND (
        approver_name LIKE CONCAT('%', #{keyword}, '%')
        )


        AND DATE_FORMAT(created_date, '%Y-%m') BETWEEN #{reportStart} AND #{reportEnd}

SELECT DATE_FORMAT(created_date, '%Y-%m') AS month, COUNT(status) AS total, SUM(CASE WHEN status = '승인' THEN 1 ELSE 0 END) AS finished, SUM(CASE WHEN status IN ('미처리', '거절') THEN 1 ELSE 0 END) AS unfinished FROM reports WHERE DATE_FORMAT(created_date, '%Y-%m') BETWEEN #{startYearMonth} AND #{endYearMonth}
AND writer_id IN
#{writerId}
GROUP BY month
