SELECT sp.*, s.name FROM salary_payments sp JOIN salaries s ON sp.salary_id = s.salary_id
( sp.payment_id LIKE CONCAT('%', #{keyword}, '%') OR sp.salary_id LIKE CONCAT('%', #{keyword}, '%') OR sp.compensation_month LIKE CONCAT('%', #{keyword}, '%') OR sp.payment_date LIKE CONCAT('%', #{keyword}, '%') OR sp.base_salary LIKE CONCAT('%', #{keyword}, '%') OR sp.position_allowance LIKE CONCAT('%', #{keyword}, '%') OR sp.meal_allowance LIKE CONCAT('%', #{keyword}, '%') OR sp.transport_allowance LIKE CONCAT('%', #{keyword}, '%') OR sp.personal_bonus LIKE CONCAT('%', #{keyword}, '%') OR sp.team_bonus LIKE CONCAT('%', #{keyword}, '%') OR sp.holiday_bonus LIKE CONCAT('%', #{keyword}, '%') OR sp.year_end_bonus LIKE CONCAT('%', #{keyword}, '%') OR sp.overtime_pay LIKE CONCAT('%', #{keyword}, '%') OR sp.income_tax LIKE CONCAT('%', #{keyword}, '%') OR sp.local_income_tax LIKE CONCAT('%', #{keyword}, '%') OR sp.national_pension LIKE CONCAT('%', #{keyword}, '%') OR sp.health_insurance LIKE CONCAT('%', #{keyword}, '%') OR sp.long_term_care_insurance LIKE CONCAT('%', #{keyword}, '%') OR sp.employment_insurance LIKE CONCAT('%', #{keyword}, '%') OR sp.deductions LIKE CONCAT('%', #{keyword}, '%') OR sp.gross_salary LIKE CONCAT('%', #{keyword}, '%') OR sp.net_salary LIKE CONCAT('%', #{keyword}, '%') OR sp.remarks LIKE CONCAT('%', #{keyword}, '%') OR s.salary_id LIKE CONCAT('%', #{keyword}, '%') OR s.employee_id LIKE CONCAT('%', #{keyword}, '%') OR s.name LIKE CONCAT('%', #{keyword}, '%') OR s.department_id LIKE CONCAT('%', #{keyword}, '%') OR s.position_id LIKE CONCAT('%', #{keyword}, '%') OR s.bank LIKE CONCAT('%', #{keyword}, '%') OR s.account_number LIKE CONCAT('%', #{keyword}, '%') OR s.annual_salary LIKE CONCAT('%', #{keyword}, '%') OR s.status LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY sp.payment_date DESC LIMIT #{pageSize} OFFSET #{offset}
